rangoS = 0.025

def probafixM1(a, k, y):
    if 2*a < k:
        S = 1 - (1 - k)**(1 - 2*a/k)
        p = (1/S)* (1-(1 - k*(1-y))**(1 - 2*a/k))
    elif 2*a == k :
        S = np.log(1.0/(1.0-k))
        p = (1/S)*np.log(1.0/(1.0-k*(1-y)))
    else :
        S = (1 - k)**(1 - 2*a/k)  - 1
        p = (1/S)* ((1 - k*(1-y))**(1 - 2*a/k)-1)
    return 1-p

def probafixWF(a,y): #para k = 0, (es un WF normal) a es la seleccion y  y es la frequencia inicial
    p = (1-np.exp(- 2*a*y))/(1 - np.exp(-2*a))
    return p
def curvas_teoricas():
    d = 0
    a0=0
    Kapa = np.linspace(0,1,20)
    S = np.linspace(-rangoS,rangoS,1000)
    #K = [3,7,10,13,16]
    K = [3,10,16]
    colores = ['b','g','r','c','m']
    for i in range(len(K)):
        P = []
        a1 = Kapa[K[i]]
        print(a1)
        kappa = (a1-a0)/(1+d+a1)
        print(kappa)
        c = (1+d)/(1+d+a1)
        for s in S:
            P.append(probafixM1(-s*100*c, kappa,0.5))
        plt.plot(-S,P,colores[i])
    P = []
    for s in S:
        P.append(probafixWF(-s*100,0.5))
    plt.plot(-S,P, color = 'gray', label='WF')
    plt.show()

def errores(vector):
    Equis = []
    Probas = []
    Err = []
    Inc = range(0,399,20)
    for i in range(20):
        equis = []
        probas = []
        err =  []
        for j in range(20):
            equis.append(vector[Inc[j]+i][0])
            probas.append(1-vector[Inc[j]+i][2])
            err.append(0.5*np.sqrt((vector[Inc[j]+i][3])/5000))
        Equis.append(equis)
        Probas.append(probas)
        Err.append(err)
    Kapa = np.linspace(0,1,20) #Ojo aqui, depende del modelo eficiencia es(0,0.9) y longevidad es (0,1)
    K = [3,10,16]    
#K = [3,7,10,13,16]
    for i in range(len(K)):
        plt.errorbar(-np.array(Equis[K[i]]),Probas[K[i]],label = 'a = '+ str( round(Kapa[K[i]],4)),yerr = Err[K[i]],fmt='.',color = colores[i])
    #mpl.rc('xtick', labelsize=17)
    mpl.rc('ytick', labelsize=17)
    plt.legend(loc='bottom right')
    plt.xlabel('$s $', fontsize = 20) #Cuidado con la etiqueta 
    plt.ylabel('Probability of fixation', fontsize = 20)
    curvas_teoricas()
    plt.show()



